import React from "react";
import "./stars.scss";
import Typed from "react-typed";
import 'semantic-ui-css/semantic.min.css'
import { Button, Icon } from 'semantic-ui-react'

class Intro extends React.Component {
  render() {
    return (
      // <div id="home" className="intro route bg-image " style={{backgroundImage: "url("+bigImage+")"}}>
      <div id="home" className="intro route bg-image background">
        <div id="stars" />
        <div id="stars2" />
        <div id="stars3" />

        <div className="intro-content display-table">
          <div className="table-cell">
            <div className="container">
              <h1 className="intro-title mb-4">Hello, <br/> I am Giorgio Giannotta</h1>
              <p className="intro-subtitle">
                <span className="text-slider-items"></span>
                <strong className="text-slider">
                  <Typed
                    strings={[
                      "iOS Developer",
                      "Front End Developer",
                      "Back End Developer",
                    ]}
                    typeSpeed={80}
                    backDelay={1100}
                    backSpeed={30}
                    loop
                  />
                </strong>
              </p>
              <p className="pt-3">
                  <Button animated="vertical" className="btn btn-primary btn js-scroll px-4" 
                  style={{backgroundColor: "#3599DA"}} href="#work" role="button">
                    <Button.Content visible style={{color: "#ffffff"}}>View My Work</Button.Content>
                    <Button.Content hidden>
                      <Icon style={{color: "#ffffff"}} name='chevron down' />
                    </Button.Content>
                  </Button>
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Intro;
